    def quit(self):
        qApp.quite()

    def send_mail(self):
        with open("mails.txt","r",encoding = "utf-8") as file:
            file_read = file.read()
            mails = file_read.split("\n")
            mail = list()
            for i in mails:
                i = i.strip("\n")
                i = i.strip(",")
                mail.append(i)
                
        sender = self.lineEdit.text()
        password = self.lineEdit_2.text()
        subject = "!!!Warning!!!"
        body = self.textEdit.toPlainText()
        for i in range(len(mail)):
            receiver = mail[i]
            
            em = EmailMessage()
            em["From"] = sender
            em["To"] = receiver
            em["Subject"] = subject
            em.set_content(body)
            context = ssl.create_default_context()
            
            with smtplib.SMTP_SSL("smtp.gmail.com",465,context = context) as smtp:
                smtp.login(sender,password)

                smtp.sendmail(sender, receiver, em.as_string())
                print("Mail Have Been Sent To", receiver)
                time.sleep(1)
        print("All Emails Have Been Sent...")
            
        
